// Get references to HTML elements
const userSlider = document.getElementById('userSlider');
const userCountDisplay = document.getElementById('userCount');
const pricingCards = document.querySelectorAll('.pricing-card');

// Define the pricing plan ranges and corresponding card indices
const userRanges = [0, 10, 20, 30]; 
const cardIndices = [0, 1, 2]; 

// Add an input event listener to the slider
userSlider.addEventListener('input', () => {
    const userCount = parseInt(userSlider.value);
    userCountDisplay.textContent = userCount;

    // Determine which pricing plan to highlight based on user count
    let highlightedCardIndex = 0;
    for (let i = 0; i < userRanges.length; i++) {
        if (userCount >= userRanges[i] && userCount < userRanges[i + 1]) {
            highlightedCardIndex = cardIndices[i];
            break;
        }
    }
    // Remove any previous highlights and add highlight to the selected card
    pricingCards.forEach((card, index) => {
        if (index === highlightedCardIndex) {
            card.classList.add('highlighted');
        } else {
            card.classList.remove('highlighted');
        }
    });
});
